import React from 'react';

export default function(props) {
    const {pagination} = props;

    function previousPage() {
        if (pagination.next) {
            props.onPaginate(props.page + 1);
        }
    }

    function nextPage() {
        if (pagination.prev) {
            props.onPaginate(props.page - 1);
        }
    }

    return (
        <div>
            <span onClick={() => paginate(-1)}>Previous</span>
            <span onClick={() => paginate(1)}>Next</span>
        </div>
    );
}
