import React from 'react';
import Card from '../components/card';
import Filters from '../components/filters';
class Home extends React.Component {
    constructor (props) {
        super (props);
        this.state = {
            CharacterDataToSend: []
        }
    }
    async componentDidMount () {
        const url = 'https://rickandmortyapi.com/api/character/ ';
        const response = await fetch(url);
        const CharacterData = await response.json();
        // let filterData = [
        //     {
        //         label: "Species",
        //         options: speciesFilterData
        //     },
        //     {
        //         label: "Gender",
        //         options: []
        //     },
        //     {
        //         label: "Origin",
        //         options: []
        //     }
        // ]
        let species = [];
        let gender = [];
        let origin = [];

        let speciesFilterData= [];
        let genderFilterData= [];
        let originFilterData = [];


        CharacterData.results.find(data => {
            species.push(data.species);
            gender.push(data.gender);
            origin.push(data.origin);
        });

        speciesFilterData = new Set(species)
        genderFilterData = new Set(gender)
        originFilterData = new Set(origin)

        this.setState({ CharacterDataToSend: CharacterData.results });
    }

    render () {
        return (
            <div>
                <Filter></Filter>
                <Card CharacterData={this.state.CharacterDataToSend}></Card>
            </div>
        )
    }
}

export default Home;
