import { createStore, applyMiddleware, compose } from 'redux';
import reducer from '../reducers/index';

let store = null;
export function getStore (initialState={}) {
    console.log("=========================== I am IN =====================");
    if (store === null) {
        store = createStore(reducer, initialState);
    }
    return store;
};
