import React from 'react'
import Card from '../components/card'

class Home extends React.Component {
    constructor (props) {
        super (props);
        this.state = {
            CharacterDataToSend: []
        }
    }
    async componentDidMount () {
        const url = 'https://rickandmortyapi.com/api/character/ ';
        const response = await fetch(url);
        const CharacterData = await response.json();
        // let filterData = [
        //     {
        //         label: "Species",
        //         options: speciesFilterData
        //     },
        //     {
        //         label: "Gender",
        //         options: []
        //     },
        //     {
        //         label: "Origin",
        //         options: []
        //     }
        // ]
        let species = [];
        let gender = [];
        let origin = [];

        let originFilterData= []
        let genderFilterData= []
        let speciesFilterData = [] 

        CharacterData.results.find(data => {
            species.push(data.species);
            gender.push(data.gender);
            origin.push(data.origin);
        });

        speciesFilterData = new Set(species)

        this.setState({ CharacterDataToSend: CharacterData.results });
    }

    render () {
        return (
            <div>
                <Card CharacterData={this.state.CharacterDataToSend}></Card>
            </div>
        )
    }
}

export default Home;
