import React, {useEffect, useState} from 'react';
import Filters from '../Filters/Filters';

const filtersData = {
    "species": {
        "label": "Species",
        "data": [
            "Human",
            "Mythology",
            "Other"
        ]
    },
};

export default function CharacterFilters(props) {
    const [filters, setFilters] = useState({
        species: [],
        gender: [],
        origin: []
    });

    useEffect(() => {
        const data = filters;

        props.characters.forEach(character => {
            if (data.species.indexOf(character.species) === -1) {
                data.species = data.species.concat(character.species);
            }

            if (data.gender.indexOf(character.gender) === -1) {
                data.gender = data.gender.concat(character.gender);
            }
        });

        setFilters(data);
    }, [props.characters]);

    return (
        <div>
            {filters.species && <Filters label="Species" filters={filters.species} sort="ASC" />}
            {filters.gender && <Filters label="Gender" filters={filters.gender} sort="DESC" />}
        </div>
    );
}
